import { useEffect, useState } from "react";
const api_base = "http://localhost:3001";

function App() {
  const [todos, setTodos] = useState([]);
  const [popupActive, setPopupActive] = useState(false);
  const [newTodo, setNewTodo] = useState("");
  const [newTime, setNewTime] = useState("");
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDay, setSelectedDay] = useState("");
  const [day, setDay] = useState("");

  useEffect(() => {
    getTodos(selectedDay);
  }, [selectedDay]);

  const tick = () => {
    setCurrentDate(new Date());
  };

  const getTodos = (day) => {
    let apiUrl = api_base + "/todos";
    if (day) {
      apiUrl += `/${day}`;
    }

    fetch(apiUrl)
      .then((res) => res.json())
      .then((data) => setTodos(data))
      .catch((err) => console.error("Error: ", err));
  };

  const completeTodo = async (id) => {
    try {
      const response = await fetch(api_base + "/todo/complete/" + id);
      const updatedTodo = await response.json();

      setTodos((todos) =>
        todos.map((todo) => {
          if (todo._id === updatedTodo._id) {
            return updatedTodo;
          }
          return todo;
        })
      );
    } catch (error) {
      console.error("Error completing todo: ", error);
    }
  };

  const addTodo = async () => {
    if (!newTodo || !newTime || !day) {
      alert("Please fill all the fields before creating a task.");
      return;
    }

    const data = await fetch(api_base + "/todo/new", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        text: newTodo,
        time: newTime,
        day: day,
      }),
    }).then((res) => res.json());

    setTodos([...todos, data]);

    setPopupActive(false);
    setNewTodo("");
    setNewTime("");
    setDay("");
  };

  const deleteTodo = async (id) => {
    try {
      await fetch(api_base + "/todo/delete/" + id, {
        method: "DELETE",
      });

      setTodos((todos) => todos.filter((todo) => todo._id !== id));
    } catch (error) {
      console.error("Error deleting todo: ", error);
    }
  };

  return (
    <div className="App">
      <h1>Welcome!!!</h1>
      <p>{currentDate.toLocaleString()}</p>
      <h4>Your tasks</h4>
      <select
        value={selectedDay}
        onChange={(e) => setSelectedDay(e.target.value)}
        onClick={() => setSelectedDay("")}
      >
        <option value="">Select a day</option>
        <option value="monday">Monday</option>
        <option value="tuesday">Tuesday</option>
        <option value="wednesday">Wednesday</option>
        <option value="thursday">Thursday</option>
        <option value="friday">Friday</option>
        <option value="saturday">Saturday</option>
        <option value="sunday">Sunday</option>
      </select>
      <div className="todos">
        {todos.length > 0 ? (
          todos.map((todo) => (
            <div
              className={"todo" + (todo.complete ? " is-complete" : "")}
              key={todo._id}
              onClick={() => completeTodo(todo._id)}
            >
              <div className="checkbox"></div>

              <div className="text">
                {todo.text} - {todo.time}
              </div>

              <div className="delete-todo" onClick={() => deleteTodo(todo._id)}>
                x
              </div>
            </div>
          ))
        ) : (
          <p>You currently have no tasks</p>
        )}
      </div>

      <div className="addPopup" onClick={() => setPopupActive(true)}>
        +
      </div>

      {popupActive ? (
        <div className="popup">
          <div className="closePopup" onClick={() => setPopupActive(false)}>
            X
          </div>
          <div className="content">
            <h3>Add Task</h3>
            <input
              type="text"
              placeholder="Add Task"
              className="add-todo-input"
              onChange={(e) => setNewTodo(e.target.value)}
              value={newTodo}
            />
            <input
              type="time"
              placeholder="Add Time"
              className="add-time-input"
              onChange={(e) => setNewTime(e.target.value)}
              value={newTime}
            ></input>
            <input
              type="text"
              placeholder="Add Date"
              className="add-date-input"
              onChange={(e) => setDay(e.target.value)}
              value={day}
            ></input>
            <div className="button" onClick={addTodo}>
              Create Task
            </div>
          </div>
        </div>
      ) : (
        ""
      )}
    </div>
  );
}

export default App;
